    // MÉTODO DE IDENTIFICAR PREPOSIÇÕES
    public function preposicoes($preposicoes)
    {
        $foo1 = 'r';
        $foo2 = 't';
        $foo3 = 'c';
        $foo4 = 'b';
        $foo5 = 'd';

        $preposicoes = explode(' ', $preposicoes);

        $cont = 0;

        $array = array();

        foreach ($preposicoes as $preposicao) :

            if (!strstr($preposicao, 'l')
                && strlen($preposicao) === 5
                && substr($preposicao, -1) != $foo1
                && substr($preposicao, -1) != $foo2
                && substr($preposicao, -1) != $foo3
                && substr($preposicao, -1) != $foo4
                && substr($preposicao, -1) != $foo5
            ) {
                array_push($array, $cont);
                $total = count($array);
                ++$cont;
            }

        endforeach;

        echo '<h2>Existem '.$total.' preposições no Texto B.</h2>';
    }

    // MÉTODO DE IDENTIFICAR VERBOS
    public function verbos($verbo)
    {
        $foo1 = 'r';
        $foo2 = 't';
        $foo3 = 'c';
        $foo4 = 'b';
        $foo5 = 'd';

        $verbos = explode(' ', $verbo);

        $cont = 0;

        $array = array();

        foreach ($verbos as $verbo) :

            $verbo = trim($verbo, ' ');

        if (strlen($verbo) >= 8
                && substr($verbo, -1) != $foo1
                && substr($verbo, -1) != $foo2
                && substr($verbo, -1) != $foo3
                && substr($verbo, -1) != $foo4
                && substr($verbo, -1) != $foo5
            ) {
            array_push($array, $cont);
            $total = count($array);
            ++$cont;
        }

        endforeach;

        echo '<h2>Há '.$total.' verbos no Texto B.</h2>';
    }

    // MÉTODO DE VERBO EM 1ª PESSOA
    public function verboPrimeiraPessoa($first)
    {
        $foo1 = 'r';
        $foo2 = 't';
        $foo3 = 'c';
        $foo4 = 'b';
        $foo5 = 'd';

        $first = explode(' ', $first);

        $cont = 0;

        $array = array();

        foreach ($first as $key => $value) :

            $value = trim($value, ' ');

        if (substr($value, 0, -7) != $foo1
                && substr($value, -1) != $foo1
                && substr($value, 0, -7) != $foo2
                && substr($value, -1) != $foo2
                && substr($value, 0, -7) != $foo3
                && substr($value, -1) != $foo3
                && substr($value, 0, -7) != $foo4
                && substr($value, -1) != $foo4
                && substr($value, 0, -7) != $foo5
                && substr($value, -1) != $foo5
                && strlen($value) >= 8
            ) {
            array_push($array, $cont);
            $total = count($array);
        }

        endforeach;

        echo '<h2>Há '.$total.' verbos em primeira pessoa no Texto B.</h2>';
    }

    // MÉTODO PARA LISTAGEM DE VOCABULARIO
    public function listadeVocabulario($vocabulario)
    {
        //MÉTODO DEIXAR NA ORDEM
        function ordem($lista1, $lista2)
        {
            if ($lista1 == $lista2) {
                return 0;
            }

            $alfabeto = 'twhzkdfvcjxlrnqmgpsb';
            $posicao1 = strpos($alfabeto, $lista1);
            $posicao2 = strpos($alfabeto, $lista2);

            $resultado = $posicao1 < $posicao2 ? -1 : 1;

            return $resultado;
        }

        // MÉTODO DE ORDENAÇÃO DAS PALAVRAS
        function ordenacao($posicao1, $posicao2)
        {
            $o = 0;

            do {
                if (!isset($posicao1[$o])) {
                    return -1;
                }

                if (!isset($posicao2[$o])) {
                    return 1;
                }
                $lista1 = $posicao1[$o];
                $lista2 = $posicao2[$o];
                $ordem = ordem($lista1, $lista2);
                ++$o;
            } while ($ordem == 0);

            return $ordem;
        }

        $arrayTextoB = array_unique(explode(' ', $vocabulario));
        usort($arrayTextoB, 'ordenacao');
        //echo wordwrap(implode(' ', $arrayTextoB));
        echo '<h2>Como seria a lista de vocabulário do Texto B: </h2>';
        echo '<p>Ordenação lexicográfica:</p>';
        echo '<textarea rows="10" cols="69">'.implode(' ', $arrayTextoB).'</textarea><br/>';
    }

    //MÉTODO DOS NUMEROS BONITOS
    public function bonitoes($numero)
    {
        $alfabeto = 'twhzkdfvcjxlrnqmgpsb';
        $numeros = explode(' ', $numero);
        $bonitao = 0;

        function bonitao($fator, $index)
        {
            $oBonitao = 1;

            for ($i = 1; $i < $index; ++$i) {
                $oBonitao = bcmul($oBonitao, 20);
            }

            $total = bcmul($oBonitao, $fator);

            return $total;
        }

        foreach ($numeros as $key => $value) :

            $i = 1;
        $bonitasso = 0;

        do {
            $pose = strpos($alfabeto, $value[$i - 1]);
            $bonitasso = bcadd($bonitasso, bonitao($pose, $i));
            ++$i;
        } while ($i <= strlen($value));

        if (($bonitasso >= 422224) && ((bcmod($bonitasso, 3) == 0))) {
            ++$bonitao;
        }

        endforeach;

        echo '<h2>No Texto B há '.$bonitao.' números bonitos distintos (atenção!).</h2>';
    }
